
public class TC009_WrapperClass {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		String Strvalue = "1234";
		
		double i=(double)(100/2);
		
		
		int num = Integer.parseInt(Strvalue);
		
		System.out.println("The addition with string is:"+(Strvalue+1000));
		
		System.out.println("The addition with Integer is:"+(num+1000));
		
		System.out.println("The value of i is:"+i);
		

	}

}
  